mapper.update:

update paper
set is_del=1
where id = #{id,jdbcType=BIGINT}

mapper.select:
select
id, subject_id, subject_name, title, teacher_id, teacher_name, score, duration, created_time, updated_time from paper
and subject_id=#{condition.subjectId,jdbcType=INTEGER}
and teacher_id=#{condition.teacherId,jdbcType=BIGINT}
and title like concat('%',#{condition.keyword,jdbcType=VARCHAR},'%')
